Ext.define('CuidaBelem.view.MapaView', {
    extend: 'Ext.Container',
    xtype: 'mapaView',
    id: 'mapaView',

    config: {

        marcador : null,

        layout: {
            pack: 'start',
            type: 'vbox',
            align: 'center'
        },

        items: [
            {
                xtype: 'titlebar',
                docked: 'top',
                title: 'Cuida Bel√©m',
                items: [
                    {
                        id: 'btVoltarMapa',
                        xtype: 'button',
                        ui: 'back',
                        text: 'Voltar'
                    }
                ]
            },
            {
                html :'<input id="tfBuscaMapa" class="controls">'
            },
            {
                xtype: 'map',
                width: '100%',
                height : '100%',
                mapOptions : {
                    zoom : 18
                }
            },
            {
                id : 'btSalvarMapa',
                xtype : 'button',
                text: 'Salvar',
                ui : 'confirm',
                docked : 'bottom',
                disabled : true
            }
        ]
    },

    alterarCoordenadas : function(latitude, longitude) {
        var _this = this;
        var mapObj = this.down('map');
        var gMap = mapObj.getMap();
        var latLng = new google.maps.LatLng(latitude, longitude);

        if(this.getMarcador() != null) {
            this.getMarcador().setPosition(latLng);
        } else {
            this.setMarcador(new google.maps.Marker({
                position: latLng,
                animation: google.maps.Animation.DROP,
                map: gMap
            }));
        }
        //cria uma tarefa com delay
        var task = Ext.create('Ext.util.DelayedTask', function() {
            gMap.setCenter(latLng);
            _this.desabilitarBotao(false);
        });

        task.delay(1000);
    },

    desabilitarBotao : function(flag) {
        var button = this.down('button');
        button.setDisabled(flag);
    }
});